$(function(){$("a").each(function(){var e=new RegExp("/"+window.location.host+"/");this.href&&!e.test(this.href)&&$(this).click(function(e){e.preventDefault();e.stopPropagation();window.open(this.href,"_blank")})});$("a.login").click(function(e){e.preventDefault()});$(".btn-group.dropdown a").click(function(e){e.preventDefault();$(this).closest(".btn-group.dropdown").find("span.selected").html($(this).html())});$("div.checkbox label").click(function(e){e.preventDefault();e.stopPropagation();$(this).find(".chkbox").toggleClass("active")})});